import React, {useState, useEffect} from 'react';
import {
    Grid,
    Container,
    CssBaseline,
    Typography,
    Card,
    TextField, 
    Button,
    FormControl,
    InputLabel,
    Select,
    MenuItem
} from '@material-ui/core';
import {Form} from 'semantic-ui-react';
import Firebase from './../firebase';

import InterestSelect from '../components/InterestSelects';
import MajorSelect from '../components/MajorSelect';

const degreeYearOptions = [
	{value: '1', label: '1'},
	{value: '2', label: '2'},
	{value: '3', label: '3'},
	{value: '4', label: '4'},
	{value: '5', label: '5'},
	{value: '6', label: '6'},
]

function Account() {

    const [hasDetails, setHasDetails] = useState(false);
    const [userDetails, setUserDetails] = useState(null);
    const [lastname, setLastName] = useState('Last Name');
    const [firstname, setFirstName] = useState('Last Name');
    const [year, setYear] = useState(0);
    const [degree, setDegree] = useState('Your DEegree');
    const [major, setMajor] = useState('Select Major');
    const [interests, setInterests] = useState([]);
    const [coursesCompleted, setCoursesCompleted] = useState('Last Name');

    const setDetailsFromFirebase = (details) => {
        console.log('here')
        console.log(details)
        if (details != null) {
            setHasDetails(true)
            setUserDetails(details)
            
            // Name stuff
            let name = details['name'].split(' ');
            setFirstName(name[0])
            setLastName(name[1])

            // Year
            setYear(details['year'])

            // Degree
            setDegree(details['degree'])
        }
    }

    const getStudentData = () => {

		let student = {}

		// Push id from firebase
		student['id'] = userDetails._id;

		student["student_data"] = {}
		student["student_data"]['name'] = firstname.concat(" ").concat(lastname)
		
        //TODO: Need degree
        student["student_data"]['degree'] = degree;
        student["student_data"]['major'] = major;
		student["student_data"]['year'] = year;
		student["student_data"]['postgraduate'] = false;
		student["student_data"]['interests'] = interests;

		return student;
	}

    // Post student details to database
    const submitAccountUpdate = () => {
        console.log(getStudentData())
        fetch('http://localhost:8080/api/update-student', {
        	method: 'POST',
        	body: JSON.stringify(getStudentData()),
        	headers: {
        		'Content-Type': 'application/json'
        	}
        })
        	.then(async (res) => {
        		if (res.status === 200) {
                    alert("Details updated")
        		} else {
        			const error = JSON.parse(await res.json());
					alert(error)
        		}
        	})
        	.catch(err => {
        		alert(err)
        });
    }

    // Get student details from database
    const fetchStudent = async () => {
        
        let firebaseDetails = null

        firebaseDetails = await Firebase.getCurrentUser();

        console.log(firebaseDetails)
        if (firebaseDetails !== null) {
            fetch(`http://localhost:8080/api/student/${firebaseDetails.l}`, {
                crossDomain: true,
                mode: 'cors',
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*',
                },
            })
                .then(async (res) => {
                    let data = await res.json();
                    setDetailsFromFirebase(data);
                })
                .catch((err) => {
                    console.log(err);
            });
        } else {
            setHasDetails(!hasDetails)
        }

    };

    const test = () => {
        console.log("test")
    }

    useEffect(() => {
        // TODO: Find something less hacky
        console.log(userDetails)
        if(userDetails == null) fetchStudent();
    }, [fetchStudent]);

    return (
        <>
            <CssBaseline />
            <Container maxWidth={false}>
                <Grid container spacing={3}>
                    <Grid item xs={8}>
                        <Typography variant='h5'>
                            {' '}
                            Edit Your Account{' '}
                        </Typography>
                    </Grid>
                </Grid>
                <br />
                <Card style={{ padding: '2%', overflow: 'visible'}}>
                    <Form>

                        <TextField
                            id="outlined-disabled"
                            label="First Name"
                            defaultValue={firstname}
                            value={firstname}
                            onChange={(event) => setFirstName(event.target.value)}
                            variant="outlined"
                            style={{ marginRight: '2%' }}
                        />

                        <TextField
                            id="outlined-disabled"
                            label="Last Name"
                            defaultValue={lastname}
                            value={lastname}
                            onChange={(event) => setLastName(event.target.value)}
                            variant="outlined"
                            style={{ marginRight: '2%' }}
                        />

                        <FormControl variant="outlined" style={{ minWidth: '120px' }}>
                            <InputLabel htmlFor="outlined-age-native-simple">Degree Year</InputLabel>
                            <Select
                            value={year}
                            onChange={test}
                            label="Degree Year"
                            >
                            <MenuItem value={1}>1</MenuItem>
                            <MenuItem value={2}>2</MenuItem>
                            <MenuItem value={3}>3</MenuItem>
                            <MenuItem value={4}>4</MenuItem>
                            <MenuItem value={5}>5</MenuItem>
                            <MenuItem value={6}>6</MenuItem>   
                            </Select>
                        </FormControl>

                        <br /><br />

                        <TextField
                            id="outlined-disabled"
                            label="Degree"
                            defaultValue={degree}
                            value={degree}
                            fullWidth="true"
                            onChange={(event) => setDegree(event.target.value)}
                            variant="outlined"
                        />

                        <br /><br />
                        <MajorSelect setMajor={test()}/>
                        <br />
                        <InterestSelect 
                            displayed_interests={null} 
                            setCurrentInterests={test()} 
                            setDisplayedInterests={test()} />
                    </Form>
                    <br /><br />
                    <Button onClick={() => submitAccountUpdate()}> Update Account </Button>
                </Card>
            </Container>
        </>
    );
}

export default Account;
