import Favorite from '@material-ui/icons/Favorite';
import React, { useState, useEffect } from 'react';

import { Typography, Grid, Container } from '@material-ui/core';

function Favorites() {  

    const [favorites, setFavorites] = useState([]);

    async function checkUserDetails(id) {
		await fetch(`http://localhost:8080/api/student/${id}`, {
			crossDomain: true,
			mode: 'cors',
			method: 'GET',
			headers: {
				'Content-Type': 'application/json',
				'Access-Control-Allow-Origin': '*',
			},
		}).then(async (res) => {
			let data = await res.json();
			sessionStorage.setItem('favorites', data.favorite_subjects);
			setFavorites(data.favorite_subjects)
		}).catch((err) => {
        }).then(() => {
		});
	}


    useEffect(() => {
        checkUserDetails()
    }, [checkUserDetails])

    if (Object.keys(favorites).length < 1) {
        return (
            <>
            <Container maxWidth={false}>
                <Typography variant='h5' style={{ paddingTop: '10px' }}> Favorite Subject List </Typography>
                <p> Use the search or recommendation function to add some favorite subjects... </p>
            </Container>
            </>
        )
    } else {
        return (
            <>
            <Container maxWidth={false}>
                <Typography variant='h5' style={{ paddingTop: '10px' }}> Favorite Subject List </Typography>
                <ul>
                    <li>gi</li>
                </ul>
            </Container>
            </>
        );    
    }
    
};

export default Favorites;
