import React, {useState, useEffect, useContext} from 'react';
import Chip from '@material-ui/core/Chip';
import { AppContext } from '../AppContext';

function CoursesCompleted(props) {

    const data = useContext(AppContext);

    const [courses, setCourses] = useState([]);
    const [subjects, setSubjects] = useState({});

    const findSubjects = () => {
      let subject_obj = {};
      data.forEach(elem => {
          if (courses.includes(elem._id)) {
              subject_obj[elem._id] = elem
          }
      })
     setSubjects(subject_obj);
   }

    useEffect(() => {
      console.log(props)
      setCourses(props.courses)
      findSubjects()
    }, [findSubjects]);

    if (courses.length > 0 && Object.keys(subjects).length > 0) {
      console.log('hi')
      console.log(courses)
      return (
        Object.keys(courses).map((index) => (
            <Chip label={courses[index] + " - " + subjects[courses[index]]['course_name']}  onDelete={() => console.log(courses[index])} color="primary" style={{ marginRight: '0.5%' }} />
        ))
      );
    } else {
      return (
        <p>No Courses Completed...</p>
      )
    }
 
}

export default CoursesCompleted;
