import React, {useState, useEffect} from 'react';
import {
    Grid,
    Container,
    CssBaseline,
    Typography,
    Card,
    TextField,
} from '@material-ui/core';
import {Form} from 'semantic-ui-react';
import firebase from './../firebase';

function Account() {

    const [firebaseID, setFirebaseID] = useState(null);
    const [userDetails, setUserDetails] = useState(null);

    const fetchStudent = async () => {
        if (firebaseID) {
            fetch(`http://localhost:8080/api/student/${firebaseID}`, {
                crossDomain: true,
                mode: 'cors',
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*',
                },
            })
                .then(async (res) => {
                    let data = await res.json();
                    setUserDetails(data);
                    console.log(data.interests);
                })
                .catch((err) => {
                    console.log(err);
                });
        }
    };

    useEffect(() => {
        let firebaseDetails = firebase.getCurrentUser();
        if (firebaseDetails) setFirebaseID(firebaseDetails.l);
        fetchStudent();
    }, [fetchStudent, firebase, setFirebaseID]);

    return (
        <>
            <CssBaseline />
            <Container maxWidth={false}>
                <Grid container spacing={3}>
                    <Grid item xs={8}>
                        <Typography variant='h5'>
                            {' '}
                            Edit Your Account{' '}
                        </Typography>
                    </Grid>
                </Grid>
                <br />
                <Card style={{ padding: '2%' }}>
                    <Form>
                        <TextField
                            id="outlined-disabled"
                            label="First Name"
                            defaultValue="First Name"
                            variant="outlined"
                            style={{ marginRight: '2%' }}
                        />
                        <TextField
                            id="outlined-disabled"
                            label="Last Name"
                            defaultValue="{userDetails}"
                            variant="outlined"
                        />
                    </Form>

                </Card>
            </Container>
        </>
    );
}

export default Account;
