import React, { useState, useEffect } from 'react';

import {
    Card,
    CardActions,
    CardContent,
    Button,
    Typography,
    CardHeader,
    IconButton
} from '@material-ui/core';

import StarOutline from '@material-ui/icons/StarOutline';
import CheckCircleOutline from '@material-ui/icons/CheckCircleOutline';

function SubjectCard(props) {

    const [title, setTitle] = useState('');
    console.log(props)

    useEffect(() => {
        setTitle(props.subject._id + " - " + props.subject.course_name)
    }, [setTitle]);

    return (
        <Card style={{marginBottom: "1%"}}>
           
           <CardContent style={{display: "flex"}}>   
               
                <Typography color='textPrimary' style={{padding: '12px', }}> {title} </Typography> 

                <IconButton aria-label="favorite" style={{marginLeft: "auto"}}>
                    <CheckCircleOutline/>
                </IconButton>
                
                <IconButton aria-label="favorite">
                    <StarOutline/>
                </IconButton>

            </CardContent>

            <CardContent>  
                <Typography paragraph> {props.subject.description} </Typography>
            </CardContent>
            
            <CardActions>
                <Button target="_blank" color="primary" size='small'  href={props.subject.link}>Learn More</Button>
            </CardActions>
        
        </Card>
    );
}

export default SubjectCard;
