import React from 'react';

function Login() {

    async function login() {
        try {
            await firebase.login(email, password)
            props.history.replace('/home');
        } catch (error) {
            alert(error.message);
        }
    }

    function login(){

        var userEmail = document.getElementById("email_field").value;
        var userPass = document.getElementById("password_field").value;
      
        firebase.auth().signInWithEmailAndPassword(userEmail, userPass).catch(function(error) {
          // Handle Errors here.
          var errorCode = error.code;
          var errorMessage = error.message;
      
          window.alert("Error : " + errorMessage);
      
          // ...
        });
      
    }

    return (
        <div style={{padding: '2%'}}>
            <center>
            <h3>Login to Course Recommender</h3>
            <input type='email' placeholder='Email...' id='email_field'  onChange={(e) => setEmail(e.target.value)}/><br /><br />
            <input type='password' placeholder='Password...' id='password_field'  onChange={(e) => setEmail(e.target.value)}/><br /><br />
            <button onclick='login()'>Login to Account</button>
            </center>
        </div>
    );
}

export default Login;
