import React, {useState, useEffect} from 'react';
import {
    Grid,
    Container,
    CssBaseline,
    Typography,
    Card,
    TextField, 
    Button
} from '@material-ui/core';
import {Form} from 'semantic-ui-react';
import firebase from './../firebase';

function Account() {

    const [firebaseID, setFirebaseID] = useState(null);
    const [userDetails, setUserDetails] = useState(null);
    const [lastname, setLastName] = useState('Last Name');
    const [firstname, setFirstName] = useState('Last Name');

    const setDetailsFromFirebase = (details) => {
        console.log('here')
        console.log(details)
        if (details != null) {
            setUserDetails(details)
            // Name stuff
            let name = details['name'].split(' ');
            setFirstName(name[0])
            setLastName(name[1])
        }
    }

    const getStudentData = () => {

		let student = {}
		let firebaseDetails = firebase.getCurrentUser()

		// Push id from firebase
		student['id'] = userDetails._id;

		student["student_data"] = {}
		student["student_data"]['name'] = firstname.concat(" ").concat(lastname)
		
        //TODO: Need degree
        student["student_data"]['degree'] = userDetails.major;
        student["student_data"]['major'] = userDetails.major;
		student["student_data"]['year'] = userDetails.yearOfDegree;
		student["student_data"]['postgraduate'] = false;
		student["student_data"]['interests'] =userDetails.current_interests;

		return student;
	}

    const submitAccountUpdate = () => {
        console.log(getStudentData())
        fetch('http://localhost:8080/api/update-student', {
        	method: 'POST',
        	body: JSON.stringify(getStudentData()),
        	headers: {
        		'Content-Type': 'application/json'
        	}
        })
        	.then(async (res) => {
        		if (res.status === 200) {
        			this.props.history.push('/home');
        		} else {
        			const error = JSON.parse(await res.json());
					alert(error)
        		}
        	})
        	.catch(err => {
        		alert(err)
        });

    }

    const fetchStudent = async () => {
        if (firebaseID) {
            fetch(`http://localhost:8080/api/student/${firebaseID}`, {
                crossDomain: true,
                mode: 'cors',
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*',
                },
            })
                .then(async (res) => {
                    let data = await res.json();
                    setDetailsFromFirebase(data);
                })
                .catch((err) => {
                    console.log(err);
                });
        }
    };

    useEffect(async () => {
        let firebaseDetails = await firebase.getCurrentUser();
        if (userDetails == null && firebaseDetails) {
            setFirebaseID(firebaseDetails.l);
            fetchStudent();
        }
        
    }, [fetchStudent, firebase, setFirebaseID]);

    return (
        <>
            <CssBaseline />
            <Container maxWidth={false}>
                <Grid container spacing={3}>
                    <Grid item xs={8}>
                        <Typography variant='h5'>
                            {' '}
                            Edit Your Account{' '}
                        </Typography>
                    </Grid>
                </Grid>
                <br />
                <Card style={{ padding: '2%' }}>
                    <Form onSubmit={() => submitAccountUpdate}>
                        <TextField
                            id="outlined-disabled"
                            label="First Name"
                            defaultValue={firstname}
                            value={firstname}
                            onChange={(event) => setFirstName(event.target.value)}
                            variant="outlined"
                            style={{ marginRight: '2%' }}
                        />
                        <TextField
                            id="outlined-disabled"
                            label="Last Name"
                            defaultValue={lastname}
                            value={lastname}
                            onChange={(event) => setLastName(event.target.value)}
                            variant="outlined"
                        />
                        <br/><br/>
                        <Button type='submit' value='Submit'> Update Account </Button>
                    </Form>
                </Card>
            </Container>
        </>
    );
}

export default Account;
