import React, {useState, useEffect} from 'react';
import {
    Grid,
    Container,
    CssBaseline,
    Typography,
    Card,
    TextField, 
    Button
} from '@material-ui/core';
import {Form} from 'semantic-ui-react';
import firebase from './../firebase';

function Account() {

    const [firebaseID, setFirebaseID] = useState(null);
    const [userDetails, setUserDetails] = useState(null);
    const [lastname, setLastName] = useState('Last Name');
    const [firstname, setFirstName] = useState('Last Name');

    const setDetailsFromFirebase = (details) => {
        console.log('here')
        if (details != null) {
            setUserDetails(details)
            // Name stuff
            let name = details['name'].split(' ');
            setFirstName(name[0])
            setLastName(name[1])
        }

    }

    const submitAccountUpdate = () =>{
        console.log('Updating')
        console.log(userDetails)

        userDetails['name'] = firstname.concat(" ").concat(lastname)

    }

    const fetchStudent = async () => {
        if (firebaseID) {
            fetch(`http://localhost:8080/api/student/${firebaseID}`, {
                crossDomain: true,
                mode: 'cors',
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*',
                },
            })
                .then(async (res) => {
                    let data = await res.json();
                    setDetailsFromFirebase(data);
                })
                .catch((err) => {
                    console.log(err);
                });
        }
    };

    useEffect(async () => {
        let firebaseDetails = await firebase.getCurrentUser();
        if (userDetails == null && firebaseDetails) {
            setFirebaseID(firebaseDetails.l);
            fetchStudent();
        }
        
    }, [fetchStudent, firebase, setFirebaseID]);

    return (
        <>
            <CssBaseline />
            <Container maxWidth={false}>
                <Grid container spacing={3}>
                    <Grid item xs={8}>
                        <Typography variant='h5'>
                            {' '}
                            Edit Your Account{' '}
                        </Typography>
                    </Grid>
                </Grid>
                <br />
                <Card style={{ padding: '2%' }}>
                    <Form onSubmit={submitAccountUpdate}>
                        <TextField
                            id="outlined-disabled"
                            label="First Name"
                            defaultValue="First Name"
                            value={firstname}
                            onChange={(event) => setFirstName(event.target.value)}
                            variant="outlined"
                            style={{ marginRight: '2%' }}
                        />
                        <TextField
                            id="outlined-disabled"
                            label="Last Name"
                            defaultValue={lastname}
                            value={lastname}
                            onChange={(event) => setLastName(event.target.value)}
                            variant="outlined"
                        />
                        <br/><br/>
                        <Button type='submit' value='Submit'> Update Account </Button>
                    </Form>
                </Card>
            </Container>
        </>
    );
}

export default Account;
